  ID: {
    type: String,
    unique: true,
    index: true,
    //denyUpdate: true,
    label: 'Unique gene ID'
  },
  type: {
    type: String,
    allowedValues: ['gene','mRNA','CDS','exon','three_prime_UTR','five_prime_UTR'],
    label: 'Interval type'
  },
  seq: {
    type: String,
    label: 'Reference sequence of this feature'
  },
