    type: Number,
    label: 'Start coordinate'
  },
  end: {
    type: Number,
    label: 'End coordinate'
  },
  score: {
    type: String,
    label: 'Score',
    custom: function(){
      if (!this.isSet){
        return 'required'
      }
      if (this.value === '.'){
        return true
      } else {
