	 updateUsers (_id,fields){
		if (! this.userId) {
			throw new Meteor.Error('not-authorized');
		}
		Meteor.users.update({'_id':_id},{$set:fields})
	},
	updateGeneInfo (geneId,update,revert){
		const userId = this.userId;
		if (! userId) {
			throw new Meteor.Error('not-authorized');
		}
		if (! Roles.userIsInRole(userId,'curator')){
			throw new Meteor.Error('not-authorized');
		}
		console.log(`Updating gene ${geneId}`)
		console.log('Update:',update)
		console.log('Revert:',revert)

		let newAttributes = [];

		if (update.hasOwnProperty('$set')){
			newAttributes = Object.keys(update['$set']).filter( key => {
				return key.startsWith('attributes.')
			}).map( key => {
				return {
					query: key,
					name: key.replace('attributes.','')
				}
			})
		}

		console.log('New attributes:', newAttributes)

		const revertString = JSON.stringify(revert);

		const gene = Genes.findOne({ID: geneId})

		Genes.update({ ID: geneId }, update, (err,res) => {
			if (!err){
				EditHistory.insert({ 
					ID: geneId, 
					date: new Date(), 
					user: userId, 
					revert: revertString
				})
				newAttributes.forEach( newAttribute => {
					Attributes.findAndModify({
						query: {
							name: newAttribute.name
						},
						update: {
							$setOnInsert: {
								name: newAttribute.name,
								query: newAttribute.query,
								show: true,
								reserved: false,
								canEdit: false
							},
							$push: {
								references: gene.reference,
								tracks: gene.track
							}
						},
						upsert: true,
						new: true
					})
				})
			}
		})
	},
	updateAttributes (_id,fields){
		if (! this.userId) {
			throw new Meteor.Error('not-authorized');
		}
		if (! Roles.userIsInRole(this.userId,'admin')){
			throw new Meteor.Error('not-authorized');
		}
		Attributes.update({'_id':_id},{$set:fields})
	},
	formatGff3 (query){
		if (! this.userId) {
			throw new Meteor.Error('not-authorized');
		}

		const genes = Genes.find(query)
		const total = genes.count();
		let counter = 0;
		const gff = genes.map(function(gene){
			counter += 1;
			let subLines = gene.subfeatures.map(function(sub){
				let subFields = [
					gene.seqid,
					gene.source,
					sub.type,
					sub.start,
					sub.end,
					sub.score,
					gene.strand,
					sub.phase,
					'ID='+sub.ID+';Parents='+sub.parents.join()
				]
				return subFields.join('\t') + '\n';
			})
			let geneFields = [
				gene.seqid,
				gene.source,
				gene.type,
				gene.start,
				gene.end,
				gene.score,
				gene.strand,
				gene.phase,
				'ID='+gene.ID
			]
			let geneLine = geneFields.join('\t') + '\n';

			//unshift adds to the beginning of the array
			subLines.unshift(geneLine);

			return subLines.join('')
		})
		return gff
	},
	initializeDownload (query,format){
		queryHash = hash(query);
		queryString = JSON.stringify(query);
		existing = Downloads.findOne({query:queryHash,format:format})
		let downloadId;
		if (existing === undefined){
			downloadId = Downloads.insert({query:queryHash,queryString:queryString,format:format})
			//return downloadId
		} else {
			downloadId = existing._id
			//return existing._id
		}
	}
})

