          <i className={`fa ${hasPreviousVersion ? 'fa-arrow-left' : 'fa-ban'}`} aria-hidden="true"></i> Older
        </button>
        <button className="btn btn-sm btn-dark pull-right" name="next" onClick={ props.selectVersion } disabled={!hasNextVersion}>
