    }
})

Template.interproscan.rendered = function(){
    const transcript = this.data.subfeatures.filter(function(x){return x.type === 'mRNA'})[0];
    const id = transcript.ID;
    const data = _.values(transcript.interproscan).sort(function(a,b){return a.start-b.start});
    const groupedData = _.groupBy(data,function(d){return getInterproId(d)})
    const interproMetaData = getInterproMetaData(groupedData)
    const DataArray = _.values(groupedData)

    const groupedDataArray = DataArray.map(function(domain){
        let domainObj = _.groupBy(domain,function(d){return d.source})
        let domainList = _.values(domainObj)
        return domainList
    })

    const lineHeight = 10;
    const nameSpacing = 140

    var margin = {top: 10, right: 10, bottom: 10, left: 10};
    var width = $('.interproscan').width() - margin.left - margin.right;
    var height = (lineHeight * data.length) - margin.top

    //set min and max values
    var start = 0;
    var end = transcript.seq.length;

    //setup x scale 
    var xScale = d3.scaleLinear()
            .domain([0,end])
            .range([nameSpacing,width])

    //setup svg
    var svg = d3.select('.interproscan').append('svg')
            .attr('width',width + margin.left + margin.right)
            .attr('height',height + margin.top + margin.bottom + 20)
        .append('g')
            .attr('class','container')
            .attr('transform','translate(' + margin.left + ',' + margin.top + ')')

    //draw axis first, so it sits nicely in the back
    var xAxis = d3.axisBottom(xScale)
            .ticks(10)
            .tickPadding(-height - margin.top - margin.bottom - 20)
            .tickSize(height + margin.top + margin.bottom)

    svg.append('g')
            .attr('class','x axis')
            .attr('transform','translate(0,10)')
            .call(xAxis)
        .selectAll('text')
            .attr('transform','rotate(0)')
            .style('text-anchor','start')

    //make domain groups
    let domainCounter = 0
    var domains = svg.selectAll('.domain')
            .data(groupedDataArray)
        .enter().append('g')
            .attr('class','domain')
            .attr('transform',function(d,i){
                domainCounter += d.length
                return 'translate(0,' + (((domainCounter - d.length) * (lineHeight + 10)) + 15) + ')'
            })

    var sources = domains.selectAll('.source')
            .data(function(source){
                return source
            })
        .enter().append('g')
            .attr('class','source')
            .attr('transform',function(d,i){
                return 'translate(0,' + i * lineHeight + ')'
            })
    //draw domains
    var rect = sources.selectAll('rect')
            .data(function(domain){
                return domain
            })
        .enter().append('rect')
            .attr('x',function(d){
                return xScale(d.start * 3)
            })
            .attr('width',function(d){
                return xScale(d.end * 3) - xScale(d.start * 3)
            })
            .attr('y',function(d,i){
                return 0//i * 15
            })
            .attr('height',7)
            .attr('rx',2)
            .attr('ry',2)
            .each(function(d,i){
                //initialize bootstrap popover for every rect, bootstrap popup dismissal is automatically taken care of by code in client/main/app-body.js
                $(this).popover({
                    title:d.name,
                    html:true,
                    content:function(){
                        let content = 'Source: ' + d.source + '<br>'
                        if (d.signature_desc) {
                            content += 'Description: ' + d.signature_desc
                        }
                        return content
                    },
                    container:$('#interproscan'),
                    placement:'top'
                })
            })
            .style('fill',function(d){
                let interpro = getInterproId(d)
                if (interpro !== 'Unintegrated'){
                    return interproMetaData[interpro].color
                } else {
                    return 'grey'
                }
            })
            .style('opacity',1)
            .style('stroke','black')
            .style('stroke-width',0.5)
    //add domain names
    var domainIds = domains.selectAll('foreignObject')
            .data(function(domain){
                return [domain[0][0]]
            })
        .enter().append('foreignObject')
            .attr('x',0)
            .attr('y',0)
            .html(function(d){
                let ipr = getInterproId(d)
                return '<button type="button" class="btn btn-default btn-xs">' + ipr + '</button>'
            })
    /*
    var names = domains.selectAll('text')
            .data(function(domain){
                console.log(domain)
                return [domain[0][0]]
            })
        .enter().append('text')
            .attr('x',0)
            .attr('y',10)
            .text(function(d){ 
                let ipr = getInterproId(d)
                if (ipr === 'Unintegrated'){
                    return 'Unintegrated'
                } else {
                    return ipr + ' ' + interproMetaData[ipr].description
                }
            })
    */


    function update(){
        width = $('.interproscan').width() - margin.left - margin.right;
        xScale.range([nameSpacing,width])
        svg.attr('width',width)
        rect.attr('x',function(d){ 
            return xScale(d.start * 3)
        })
        .attr('width',function(d){
            return xScale(d.end * 3) - xScale(d.start * 3)
        })

        xAxis.scale(xScale)
        svg.select('g.x.axis').call(xAxis)
    }

    $(window).resize(function() {
        update()
    }).trigger("resize");
}

import { Meteor } from 'meteor/meteor';
import { Template } from 'meteor/templating';

import d3 from 'd3';
import { schemeSet3 } from 'd3-scale-chromatic';

import { Orthogroups } from '/imports/api/genes/orthogroup_collection.js';

import './orthogroup.html';
import './orthogroup.scss';

Meteor.subscribe('orthogroups');

function parseNewick(a){
	//Copyright 2011 Jason Davies https://github.com/jasondavies/newick.js
	for(var e=[],r={},s=a.split(/\s*(;|\(|\)|,|:)\s*/),t=0;t<s.length;t++){
		var n=s[t];switch(n){case"(":var c={};r.children=[c],e.push(r),r=c;break;case",":var c={};e[e.length-1].children.push(c),r=c;break;case")":r=e.pop();break;case":":break;default:var h=s[t-1];")"==h||"("==h||","==h?r.name=n:":"==h&&(r.value=parseFloat(n))}}
		return r
	}

Template.orthogroup.helpers({
	orthogroup:function(){
		return Orthogroups.findOne({'ID':this.orthogroup})
	},
	alignment: function(orthogroup){
		const alignment = orthogroup.alignment
		const headers = alignment.map(function(x){ 
			let header = x.header.split('&#46;').join('.')
			return header
		})
		const longestHeader = Math.max(...headers.map(function(header){return header.length}))
		const paddedHeaders = headers.map(function(header){
			let paddingLength = (longestHeader - header.length) + 1;
			let padding = '\xA0'.repeat(paddingLength);
			return header + padding
		})
		const sequences = alignment.map(function(x){return x.sequence})
		const zipped = _.zip(paddedHeaders,sequences)
		const merged = zipped.map(function(x){return x[0] + x[1] })

		return merged
	},
	tree: function(orthogroup){
		const treeString = orthogroup.phylogenetic_tree;
		const tree = parseNewick(treeString);
		return tree;
	},
	alignmentScore: function(orthogroup){
		const alignment = orthogroup.alignment
		//only take the sequences
		const sequences = alignment.map(function(x){return x.sequence})

		//alignment is square, so length is equal to length of first sequence
		const alignmentLength = sequences[0].length

		//arbitrary cutoff
		const cutoff = 0.5;

		//all nucleotides
		let allNucleotides = 0

		//informative nucleotides
		let infoNucleotides = 0

		//loop over column index, characters
		for (var charIndex = 0;charIndex < alignmentLength;charIndex++){
			//get current character/column for every sequence
			let chars = sequences.map(function(seq){return seq[charIndex]})

			//get non gap characters
			let nonGapChars = chars.filter(function(c){ if (c !== '-') return c })

			allNucleotides += nonGapChars.length

			if ((nonGapChars.length / chars.length) > cutoff) {
				infoNucleotides += nonGapChars.length
			}
		}
		return (infoNucleotides / allNucleotides).toFixed(3)
	}
})

Template.orthogroup.rendered = function(){
	const ID = this.data.ID + '.1';
	const og = Orthogroups.findOne({'ID':this.data.orthogroup});
	const treeData = parseNewick(og.phylogenetic_tree);
	const hierarchy = d3.hierarchy(treeData)
		.sum(function(d){ return d.value })
		.sort(function(a, b){ return b.height - a.height || b.value - a.value; });

	let margin = {top: 10, right: 10, bottom: 10, left: 20};
    let width = $('.experiments').width() - margin.left - margin.right;
    let height = (og.alignment.length * 20) - margin.bottom - margin.top;


	const cluster = d3.cluster()
		.size([height,width - 200])
		.separation(function(a, b){ return 1 })

	const nodes = cluster(hierarchy).descendants();
	const links = cluster(hierarchy).links();

	let svg = d3.select('.tree').append('svg')
		.attr('height', height + margin.bottom + margin.top)
		.attr('width', width + margin.left + margin.right)

	let chart = svg.append('g')

	let link = chart.append('g')
			.attr('class','links')
		.selectAll('path')
			.data(links)
		.enter().append('path')
			//.each(function(d){ d.target.linkNode = this; })
			.attr('d',function(d){ return  step(d.source.x, d.target.x, d.source.y, d.target.y) })
			.style('stroke','black')
			.style('fill','none')
			.attr('transform','translate(5,0)')

	chart.append('g')
			.attr('class','labels')
		.selectAll('text')
			.data(nodes.filter(function(d){ return !d.children }))
		.enter().append('text')
			//.attr('dy','.10em')
			.attr('class', function(d) {
				if (d.data.name === ID){
					return 'label current'
				} else {
					return 'label'
				}
			})
			.style('text-anchor','start')
			.attr('transform',function(d){ return 'translate(' + ( d.y + 10 ) + ',' + ( d.x + 3 ) +')' })
			.text(function(d){ return d.data.name })

}

function step(startX,endX,startY,endY){
	return 'M' + startY + ',' + startX + ' L' + startY + ',' + endX + ' ' + endY + ',' + endX;
}

import React from 'react';
