class Register extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      username: '',
      validUsername: true,
      email: '',
      validEmail: true,
      password: '',
      passwordRepeat: '',
      validPassword: true
    }
  }

  submit = (event) => {
    event.preventDefault()
    console.log('submit')
    console.log(this.state)
    if (this.state.password !== this.state.passwordRepeat){
      alert('Passwords do not match!')
      this.setState({
        password: '',
        passwordRepeat: '',
        validPassword: false
      })
    } else {
      const userData = {
        username:  this.state.username,
        email: this.state.email,
        password: this.state.password
      }
      Accounts.createUser(userData, (err,res) => {
        if (err){
          this.setState({
            password: '',
            passwordRepeat: ''
          })
          alert(err.reason)
          if (err.reason === 'Username already exists.'){
            this.setState({
              validUsername: false
            })
          } else if (err.reason === 'Email already exists.'){
            this.setState({
              validEmail: false
            })
          }
        } else {
          FlowRouter.redirect('/')
        }
      })
    }
  }

  handleChange = (event) => {
    this.setState({
      [event.target.id] : event.target.value
    })
    switch (event.target.id){
      case 'username': {
        this.setState({
          validUsername: true
        })
        break;
      }
      case 'email': {
        this.setState({
          validEmail: true
        })
        break;
      }
      case 'password':
      case 'password-repeat': {
        this.setState({
          validPassword: true
        })
        break
      }
    }
  }

  filledInAllFields = () => {
    return (
      this.state.username.length > 0 &&
      this.state.email.length > 0 &&
      this.state.password.length > 0 &&
      this.state.passwordRepeat.length > 0
    )
  }

  render(){
    return (
      <div className="row justify-content-center">
        <div className="col-3">
        <h1 className="text-center login-title">Create new Genebook account</h1>
          <div className="register">
            <form className="form-register" onSubmit={this.submit} >
              <UsernameInput 
                userName = {this.state.username} 
                validUsername = {this.state.validUsername}
                handleChange = {this.handleChange} />
              <EmailInput 
                email = {this.state.email} 
                validEmail = {this.state.validEmail}
                handleChange = {this.handleChange} />
              <PassWordInput 
                password = {this.state.password} 
                passwordRepeat = {this.state.passwordRepeat} 
                validPassword = {this.state.validPassword}
                handleChange = {this.handleChange} />
              <button 
                className={`btn btn-lg btn-block ${ this.filledInAllFields() ? 'btn-success' : 'btn-outline-success' }`} 
                type="submit"
                disabled = { !this.filledInAllFields() }>
                {
                  this.filledInAllFields() ? 'Sign up' : 'Please fill in all fields'
                }
              </button>
            </form>
          </div>
        </div>
      </div>
    )
  }
}

export default withTracker(props => {
  return {}
})(Register);

