class UserProfile extends React.Component {
  constructor(props) {
    super(props);
    console.log('constructing user profile component')
    this.state = this.props.user
  }

  componentWillReceiveProps(nextProps){
    this.setState(nextProps.user)
  }

  resetPassword = (event) => {
    event.preventDefault();
    alert('This currently does nothing')
    console.log('resetPassword');
  }

  deleteAccount = (event) => {
    event.preventDefault();
    alert('This currently does nothing')
    console.log('deleteAccount')
  }

  saveChanges = (event) => {
    event.preventDefault();
    const updateQuery = diff(this.props.user,this.state);
    console.log(updateQuery);
    console.log(this.props)
    updateUserInfo.call({
      userId: this.props.user._id,
      update: updateQuery
    })
  }

  cancelChanges = (event) => {
    event.preventDefault();
    this.setState(this.props.user);
  }

  handleChange = (event) => {
    const key = event.target.id;
    const value = event.target.value;
    const name = event.target.name;

    switch (key){
      case 'username':
        this.setState({username: value})
        break
      case 'email':
        const index = parseInt(name[name.length - 1]);
        const emails = this.state.emails.slice(); //empty slice to copy email array and not modify state
        emails[index] = value;
        this.setState({emails: emails})
        break
      case 'firstname':
        this.setState({
          profile: update(this.state.profile, {
            first_name: {
              $set: value
            }
          })
        })
        break
      case 'lastname':
        this.setState({
          profile: update(this.state.profile, {
            last_name: {
              $set: value
            }
          })
        })
        break
    }
  }

  updateRoles = newRoles => {
    const newState = update(this.state, {
      roles: {
        $set: newRoles.map( role => role.value)
      }
    })
    this.setState(newState)
  }

  render(){
    return (
      this.props.loading ?
      <div className='user-profile'>LOADING</div> :

      <div className="user-profile container">
        <form className="user-profile card">
          <div className="card-header">
            <h3>User profile</h3>
          </div>
          <div className="card-body">
            <div className="form-group">
              <label htmlFor="username" className="control-label">Username</label>
              <input 
                type="text" 
                className="form-control form-control-sm" 
                id="username"
                onChange={this.handleChange} 
                value={this.state.username} />
                <small id="emailHelp" className="form-text text-muted">
                  Your username will be visible to other users and must be unique. <b>required</b>
                </small>
            </div>
            <div className="form-group">
              <label htmlFor="firstname" className="control-label">First name</label>
              <input 
                type="text" 
                className="form-control form-control-sm" 
                id="firstname" 
                onChange={this.handleChange}
                value={this.state.profile.first_name}
                placeholder="First name" />
            </div>
            <div className="form-group">
              <label htmlFor="lastname" className="control-label">Last name</label>
              <input 
                type="text" 
                className="form-control form-control-sm" 
                id="lastname" 
                onChange={this.handleChange}
                value={this.state.profile.last_name}
                placeholder="Last name" />
            </div>
            {
              this.state.emails.map( (email, i) => {
                //start counting at 1
                let index = i + 1;
                return (
                  <div className="form-group" key={`email${index}`}>
                    <label htmlFor={`email${index}`} className="control-label">{`Email address ${index}`}</label>
                    <input 
                      type="email" 
                      className="form-control form-control-sm" 
                      id={`email${index}`} 
                      onChange={this.handleChange}
                      value={email.address} />
                  </div>
                )
              })
            }
            <UserRoles 
              userRoles = {this.state.roles}
              allRoles = {this.props.allRoles}
              isAdmin = {Roles.userIsInRole(Meteor.userId()),'admin'} 
              onChange = {this.updateRoles} />
          </div>
          <div className="card-footer">
            <UserProfileButtons 
              oldState = {this.props.user}
              newState = {this.state}
              cancelChanges = {this.cancelChanges}
              saveChanges = {this.saveChanges}
              resetPassword = {this.resetPassword}
              deleteAccount = {this.deleteAccount}/>
          </div>
        </form>
      </div>
    )
  }
}

export default withTracker(props => {
  const subscription = Meteor.subscribe('users');
  const userId = FlowRouter.getParam('_id');
  const userProfile = userId ? Meteor.users.findOne({_id: userId}) : Meteor.user();
  const allRoles = Meteor.users.find({}).fetch().reduce((roles, user) => {
    return roles.concat(user.roles)
  },[])
  const uniqueRoles = [...new Set(allRoles)]
   return {
    user: pick(userProfile, ['_id','username', 'emails', 'profile', 'roles']),
    loading: !subscription.ready(),
    allRoles: uniqueRoles
   }
})(UserProfile);

import React from 'react';

/**
 * Higher order component for conditional rendering, to be used with compose
 * https://www.robinwieruch.de/gentle-introduction-higher-order-components/
 * @param  {Function}   conditionalRenderingFn Function returning a boolean for condional rendering
 * @param  {Function} EitherComponent       Component to render if conditionalRenderingFn evaluates to true
 * @return {Function}                          
 */
export const withEither = (conditionalRenderingFn, EitherComponent) => (Component) => (props) =>
  conditionalRenderingFn(props)
    ? <EitherComponent />
    : <Component { ...props } />


/**
 * Helper function to see if reactive dataTracker is still loading
 * @param  {Boolean} options.loading 
 * @return {Boolean}                 
 */
export const isLoading = ({ loading }) => {
  return loading
}

/**
 * Simple React Loading component
 * @return {Function} React Loading component
 */
export const Loading = () => {
  return (
    <div>
      Loading ...
    </div>
  )
}
